
$(document).ready(function () {
    $("#gototop").hide()

    $(window).scroll(function () {
        if ($(this).scrollTop() >= 150)
            $("#gototop").show("slow")
        else
            $("#gototop").hide("slow")
    })
    $("#gototop").click(function () {
        $("html, body").animate({
            scrollTop: 0
        }, 1500);
    })

})
$('#clickchuong').click(function(){
    $("html, body").animate({
        scrollTop: 1100
    }, 1000)
    
});


