
 var state = false;
 function toggle(){
     if(state){
         document.getElementById("pass").setAttribute("type","password");
         document.getElementById("repass").setAttribute("type","password");
         document.getElementById("eye").className='fa-solid fa-eye-slash';
         state=false;
     }
     else{
        document.getElementById("repass").setAttribute("type","text");
         document.getElementById("pass").setAttribute("type","text");
         document.getElementById("eye").className="fa-solid fa-eye";
         state=true;
     }
 }
 
 
    