$('.toggle-product').change(function () {
    var kichhoat = $(this).prop('checked') == true ? 0 : 1;
    var truyen_id = $(this).data('id');
    $.ajax({
        type: 'GET',
        dataType: 'json',
        url: '/ChangeStatusProduct',
        data: {
            'kichhoat': kichhoat,
            'truyen_id': truyen_id
        },
        success: function (data) {
            $('#notifDiv').fadeIn();
            $('#notifDiv').css('background', 'green');
            $('#notifDiv').text('Updated Successfully');
            setTimeout(() => {
                $('#notifDiv').fadeOut();
            }, 1500);
        }
    });
});
